public class ValueCheck {
	public String getCardValue(String pixels){
		if(pixels.equals("   00000     0000000   0000 0000  000   000         00        000       0000     00000     00000     0000      0000   00  000000000  000000000            "))return "2";
		if(pixels.equals("    000       00000     000 000    00   00        000      0000       00000        0000        000  00     00  0000 0000   0000000     00000              "))return "3";
		if(pixels.equals("     000       0000       0000      00000     000 00     000 00    000  00    000000000  000000000       00         00       000000     000000            "))return "4";
		if(pixels.equals("  0000000    0000000   000        000        0000000    00000000   0000 0000        000         00  000   000  0000 0000   0000000     00000              "))return "5";
		if(pixels.equals("   00000     0000000   0000 000   000        0000000    00000000   0000 0000  000   000  00     00  000   000  0000 0000   0000000     00000              "))return "6";
		if(pixels.equals(" 000000000  000000000  00    000  00    000       0000       000       0000       000       0000       000        000        00         00                "))return "7";
		if(pixels.equals("    000       00000     000 000    00   00    000 000     00000     0000000   0000 0000  000   000  00     00  0000 0000   0000000     00000              "))return "8";
		if(pixels.equals("   00000     0000000   0000 0000  000   000  00     00  000   000  000  0000   00000000    0000000        000   00   000   0000000     00000              "))return "9";
		if(pixels.equals(" 00  0000   00 000000  00 000000  00 00  00  00 00  00  00 00  00  00 00  00  00 00  00  00 00  00  00 00  00  00 000000  00 000000  00  0000             "))return "10";
		if(pixels.equals("     00000      00000        00         00         00         00         00         00   00    00   000  000   000000000   000000      0000               "))return "J";
		if(pixels.equals("   00000     0000000  00000 0000  000   000  00     00  00     00  00     00 00000   00 000000  00  00 000000  000 000000  00000000    00000000       0000"))return "Q";
		if(pixels.equals(" 0000 0000  0000 0000   00   00    00  000    00 000     00000      000000     00 000     00  000    00  000    00   00   0000 0000  0000 0000            "))return "K";
		if(pixels.equals("    000        000       00000      00000      00000     000 000    000 000    0000000   000000000  000   000  000   000 00000 0000000000 00000           "))return "A";	
		return "";
	}
}